import React, { useState, useEffect } from 'react';

function Pets() {
  const [petsData, setPetsData] = useState([]);
  const [petName, setPetName] = useState('');
  const [petDescription, setPetDescription] = useState('');
  const [petBreed, setPetBreed] = useState('');
  const [petSpecies, setPetSpecies] = useState('');
  const [petContact, setPetContact] = useState('');

  useEffect(() => {
    const fetchPets = async () => {
      const response = await fetch('https://pets-final.onrender.com/pets');
      const data = await response.json();
      setPetsData(data.data);
    };

    fetchPets();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const petData = {
      name: petName,
      description: petDescription,
      breeds: petBreed,
      species: petSpecies,
      contact: petContact,
      photos: petPhoto,
    };
    const response = await fetch('https://pets-final.onrender.com/pets', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(petData),
    });
    const data = await response.json();
    setPetsData([...petsData, data.data]);
    setPetName('');
    setPetDescription('');
    setPetBreed('');
    setPetSpecies('');
    setPetContact('');

  };

  return (
    <div>
      <h2>Add a new pet:</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Name:
          <input
            type="text"
            value={petName}
            onChange={(e) => setPetName(e.target.value)}
          />
        </label>
        <br />
        <label>
          Description:
          <textarea
            value={petDescription}
            onChange={(e) => setPetDescription(e.target.value)}
          ></textarea>
        </label>
        <br />
        <label>
          Breed:
          <input
            type="text"
            value={petBreed}
            onChange={(e) => setPetBreed(e.target.value)}
          />
        </label>
        <br />
        <label>
          Species:
          <input
            type="text"
            value={petSpecies}
            onChange={(e) => setPetSpecies(e.target.value)}
          />
        </label>
        <br />
        <label>
          Contact:
          <input
            type="text"
            value={petContact}
            onChange={(e) => setPetContact(e.target.value)}
          />
        </label>
        <label>
          Photo:
          <input
            type="text"
            value={petContact}
            onChange={(e) => setPetContact(e.target.value)}
          />
        </label>
        <br />
        <button type="submit">Add Pet</button>
      </form>
      <hr />
      <h2>All Pets:</h2>
      {petsData.map((pet) => (
        <div key={pet.id}>
          <h2>{pet.name}</h2>
          <p>{pet.description}</p>
          <p>Breed: {pet.breeds}</p>
          <p>Species: {pet.species}</p>
          <p>Contact: {pet.contact}</p>
          <img src={pet.photos} alt={pet.name} />
        </div>
      ))}
    </div>
  );
}

export default Pets;
