import React, { useState, useEffect, useRef } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import { FaBars} from "react-icons/fa";
import { social } from "./data"
import Pets from "../Pages/Pets";
import Home from "../Pages/Home";
import AddPetForm from "../Pages/AddPetForm";
//import SignupForm from "../Pages/SignupForm";



const Navbar = () => {
const [showLinks, setShowLinks] = useState(false);
  const linksContainerRef = useRef(null);
  const linksRef = useRef(null);

  useEffect(() => {
    const linksHeight = linksRef.current.getBoundingClientRect().height;
    if (showLinks) {
      linksContainerRef.current.style.height = `${linksHeight}px`;
    } else {
      linksContainerRef.current.style.height = '0px';
    }
  }, [showLinks]);
  return (
    <Router>
    <nav>
      <div className="nav-center">
        <ul>
        <li className="logo nav-header">
              <Link to="/">Petfinder</Link>
            </li>
            <button
            className="nav-toggle"
            onClick={() => setShowLinks(!showLinks)}
          >
            <FaBars />
          </button>
          <li className="links">
              <Link to="/pets">Pets</Link>
            </li>
            <li>
              <Link to="/add-pet">Add Pet</Link>
            </li>

            <li>
            <div class="navbar bg-primary" data-bs-theme="dark">
        {/* <div class="btn btn-dark" data-netlify-identity-menu></div> */}
        <div class="btn btn-light" data-netlify-identity-button>Login/Signup: </div> 
        </div>

            </li>

        </ul>

        {/* <div className="links-container" ref={linksContainerRef}>
          <ul className="links" ref={linksRef}>
            {links.map((link) => {
              const { id, url, text } = link;
              return (
                <li key={id}>
                  <a href={url}>{text}</a>
                </li>
              );
            })}
          </ul>
        </div>  */}
        <ul className="social-icons">
          {social.map((socialIcon) => {
            const { id, url, icon } = socialIcon;
            return (
              <li key={id}>
                <a href={url}>{icon}</a>
              </li>
            );
          })}
        </ul>
      </div>
    </nav>
    <div>
      <div>
        <Routes>
        <Route path="/" element={<Home />} />
              <Route path="/pets" element={<Pets />} />
              <Route path="/add-pet" element={<AddPetForm />} />


        </Routes>
      </div>
      <div className="footer">
          <p>&copy; 2023 PetClub. All rights reserved.</p>
        </div>
    </div>


    </Router>
  );
};

export default Navbar;
